settings.custom_liquid:
{% assign difference = 7900 | minus: cart.total_price
 %}
{% if cart.total_price
 == 0 %}
    <h4 id="shippingMsg">Free shipping for orders over $79 (Local orders only)</h4>
{% elsif cart.total_price
 < 7900 %}
    <h4 id="shippingMsg">Only {{ difference | money }} away from free shipping (Local orders only)</h4>
{% else %}
    <h4 id="shippingMsg">Congratulations! You've got free shipping (Local orders only)</h4>
{% endif %}

<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
<script>
function updateShippingMsg() {
    $.getJSON('/cart.js', function(cart) {
        var difference = 7900 - cart.total_price
;
        var new_msg = '';
        if (cart.total_price
 == 0) {
            new_msg = 'Free shipping for orders over $79 (Local orders only)';
        } else if (cart.total_price
 < 7900) {
            new_msg = 'Only $' + (difference / 100).toFixed(2) + ' away from free shipping (Local orders only)';
        } else {
            new_msg = 'Congratulations! You\'ve got free shipping (Local orders only)';
        }
        $('#shippingMsg').text(new_msg);
    });
}

$(document).ready(function() {
    // Update message when page loads
    updateShippingMsg();

    // Update message when item added to cart
    $('body').on('click', '.product-form__submit', function() {
        setTimeout(updateShippingMsg, 500);
    });

    // Update message when item removed from cart
    $('body').on('click', 'cart-remove-button', function() {
        setTimeout(updateShippingMsg, 500);
    });

    // Update message when additional button 1 is clicked
    $('body').on('click', '.quantity__button', function() {
        setTimeout(updateShippingMsg, 500);
    });

});
</script>
